import React from 'react';
import './App.css'

class App extends React.Component {

   state = {
       searchTerm:'',
       selectedCountry:'a',
       selectedCountryStats:[],
   } 


   componentDidMount(){




    const selectedCountry = this.state.selectedCountry;
           //API request from country rest full apis
    fetch(`https://restcountries.eu/rest/v2/name/${selectedCountry}`)

       .then(res => res.json())
       .then(json => {
           this.setState ({

               selectedCountryStats:json
           })

           console.log(json[0].name);
       })
   }


    handleSubmit = (event) => {

        event.preventDefault();

    }

   handleChange = (event) => {
  const  handleErrors = (res) => {
           if(!res.ok){
                            this.setState ({
               selectedCountryStats:[{name:'empty',flag:'empty'}],



           })
           }
           return res;
       }
         this.setState({
            selectedCountry:event.target.value,//important event.target.value
        });
       const activeCountry = this.state.selectedCountry;
     console.log(activeCountry);

        fetch(`https://restcountries.eu/rest/v2/name/${activeCountry}`)
         .then(handleErrors)
       .then(res => res.json())
       .then(json => {


                    this.setState ({
               selectedCountryStats:json,
               searchTerm:activeCountry,


           })



       })

   }


    render () {

        return (

            <div className="App">


                   <form onSubmit={this.handleSubmit}>
                      <div>Search for a country</div>
                       <input type="text" value={this.state.selectedCountry} onChange={this.handleChange}></input>       
                   </form>

                   {this.state.selectedCountryStats.map(item=>

                                                        <div>

                                                            <div> {item.name} </div>
                                                           <div> <div className="flag-wrapper"><img src={item.flag} alt={item.flag}/></div></div>
                                                         </div>



                                                       ) }



            </div>
        )
    }

}

export default App;
